properties.query:
let MACaddr_regex = @'^[a-fA-F0-9]{2}(:[a-fA-F0-9]{2}){5}$';
  let FQDN_regex = @'^(?:([a-zA-Z0-9-]+)\.)?([a-zA-Z0-9-]{1,61})\.([a-zA-Z0-9]{2,7})$';
  let Hostname_regex = @'^[a-zA-Z0-9-]{1,61}$';
  let MD5_regex = @'[a-zA-Z0-0]{32}';
  let SHA1_regex = @'[a-zA-Z0-0]{40}';
  let SHA256_regex = @'[a-zA-Z0-0]{64}';
  let SHA512_regex = @'[a-zA-Z0-0]{128}';
  let DnsQueryTypeName = materialize (externaldata (value: string)
    [@"https://www.iana.org/assignments/dns-parameters/dns-parameters-4.csv"] with (format="csv", IgnoreFirstRecord=true));
  let DnsResponseCodeName = materialize (externaldata (code: string, value: string)
    [@"https://www.iana.org/assignments/dns-parameters/dns-parameters-6.csv"] with (format="csv", IgnoreFirstRecord=true) | project value);
  let DnsQueryClassName = materialize (externaldata (dec: string, dex: string, value: string)
    [@"https://www.iana.org/assignments/dns-parameters/dns-parameters-2.csv"] with (format="csv", IgnoreFirstRecord=true) | project value);
  let ASimFields = materialize (externaldata (ColumnName: string, ColumnType: string, Class: string, Schema: string, LogicalType:string, ListOfValues: string)
    [@"https://raw.githubusercontent.com/Azure/Azure-Sentinel/master/ASIM/dev/ASimTester/ASimTester.csv"] with (format="csv", IgnoreFirstRecord=true)
    | where Schema =~ selected_schema);
  T 
  | extend f=pack_all() 
  | mv-expand f
  | project f 
  | extend ColumnName = tostring(bag_keys(f)[0])
  | extend value = f[ColumnName]
  | extend type=gettype(value)
  | distinct ColumnName, tostring(value), type
  | lookup ASimFields on ColumnName
  | extend Result = case(      
      ColumnType != "" and type != "null" and ColumnType != type, strcat ("(0) Error: type mismatch for column [", ColumnName, "]. It is currently [", type, "] and should be [", ColumnType, "]"),
      Class == "Mandatory" and value == "", strcat ("(0) Error: Missing mandatory field [", ColumnName, "]"),
      Class == "Recommended" and value == "", strcat ("(1) Warning: Missing recommended field [", ColumnName, "]"),
      Class == "Alias" and value == "", strcat ("(1) Warning: Missing alias [", ColumnName, "]"),
      LogicalType == "Enumerated" and ListOfValues != "" and ListOfValues !has value, "Invalid Value",
      (LogicalType == "MAC Address") and (value != "") and not (value matches regex MACaddr_regex), "Invalid Value", 
      (LogicalType == "IP Address") and (value != "") and not(ipv4_is_match(value, "0.0.0.0",0)) and not(ipv6_is_match(value, "::1",0)), "Invalid Value",
      (LogicalType == "FQDN") and (value != "") and not (value matches regex FQDN_regex), "Invalid Value",
      (LogicalType == "GUID") and (value != "") and isnull(toguid(value)), "Invali  d Value",
      (LogicalType == "Hostname") and (value != "") and not (value matches regex Hostname_regex), "Invalid Value", 
      (LogicalType == "RiskLevel") and (value != "") and (toint(value) < 0 or toint(value) > 100), "Invalid Value",
      (LogicalType == "DnsQueryClassName") and (value != "") and value !in (DnsQueryClassName), "Invalid Value",
      (LogicalType == "DnsResponseCodeName") and (value != "") and value !in (DnsResponseCodeName), "Invalid Value",
      (LogicalType == "DnsQueryTypeName") and (value != "") and value !in (DnsQueryTypeName), "Invalid Value",
      (LogicalType == "MD5") and (value != "") and not (value matches regex MD5_regex), "Invalid Value", 
      (LogicalType == "SHA1") and (value != "") and not (value matches regex SHA1_regex), "Invalid Value", 
      (LogicalType == "SHA256") and (value != "") and not (value matches regex SHA256_regex), "Invalid Value", 
      (LogicalType == "SHA512") and (value != "") and not (value matches regex SHA512_regex), "Invalid Value", 
      'None'
     )
    | where Result != "None"
    | summarize values = make_set(value, 10) by Result, ColumnName
    | extend Result = iif (Result == "Invalid Value", strcat ("(0) Error: Invalid value(s) (up to 10 listed) for field [", ColumnName, "]: ", tostring(values)), Result)
    | distinct Result
    | sort by Result asc
